mapper.select:
select COALESCE(sum(bill_money),0) from record_account where classify_type = #{classifyType} and user_id =
#{userId}

    and date_format(record_time , '%Y') = #{dateTime}


    and date_format(record_time , '%Y%m') = #{dateTime}


    and date_format(record_time , '%Y%m%d') = #{dateTime}

SELECT
ra.record_account_id,
ra.classify_id,
ra.classify_name,
ra.classify_type,
ra.remark,
ra.bill_money,
icon.icon_name,
icon.icon_id,
ra.record_time
FROM record_account ra, icon, classify cl
WHERE icon.icon_id = cl.icon_id
AND cl.classify_id = ra.classify_id
AND ra.user_id = #{userId}

    AND date_format(ra.record_time, '%Y%m') = #{startDate}


    AND date_format(ra.record_time, '%Y%m%d') = #{startDate}


    AND date_format(ra.record_time, '%Y%m%d') >= #{startDate}
    AND date_format(ra.record_time , '%Y%m%d') <= #{endDate}

order by ra.record_time desc


    limit #{startPage}, #{pageSize};

SELECT(CASE DATE_FORMAT(ra.record_time, '%w') WHEN 0 THEN DATE_FORMAT(ra.record_time, '%m月%d日 星期日') WHEN 1 THEN DATE_FORMAT(ra.record_time, '%m月%d日 星期一') WHEN 2 THEN DATE_FORMAT(ra.record_time, '%m月%d日 星期二') WHEN 3 THEN DATE_FORMAT(ra.record_time, '%m月%d日 星期三') WHEN 4 THEN DATE_FORMAT(ra.record_time, '%m月%d日 星期四') WHEN 5 THEN DATE_FORMAT(ra.record_time, '%m月%d日 星期五') WHEN 6 THEN DATE_FORMAT(ra.record_time, '%m月%d日 星期六') END) date_str, (SELECT SUM(bill_money) FROM record_account WHERE classify_type = '0' AND record_time = ra.record_time) day_expense, ra.*, icon.* FROM record_account ra, icon, classify cl WHERE icon.icon_id = cl.icon_id AND cl.classify_id = ra.classify_id AND ra.user_id = #{userId}
AND date_format(ra.record_time, '%Y%m') = #{startDate}
AND date_format(ra.record_time, '%Y%m%d') = #{startDate}
AND date_format(ra.record_time, '%Y%m%d') >= #{startDate} AND date_format(ra.record_time , '%Y%m%d') <= #{endDate}
order by ra.record_time desc limit #{startPage}, #{pageSize};
